    def set_transformed_image(self):
        try:
            image = Image.open(self.tranform_img).convert("L")
            print(image.mode)
            mode = image.mode

            factor = self.contrast_slider.value() / 10
            image = ImageEnhance.Contrast(image).enhance(factor)

            factor = self.brightness_slider.value() / 10
            image = ImageEnhance.Brightness(image).enhance(factor)

            factor = self.sharpness_slider.value() / 10
            image = ImageEnhance.Sharpness(image).enhance(factor)

            pixmap = self.__img_2_pixmap(image, mode)
            self.textBrowser_2.setPixmap(pixmap)
        except Exception as e:
            print(e)

    def update_transformed_image(self):
        try:
            image = Image.open(self.tranform_img).convert("L")
            mode = image.mode

            # Update Contrast
            factor = self.contrast_slider.value() / 10
            image = ImageEnhance.Contrast(image).enhance(factor)

            # Update Brightness
            factor = self.brightness_slider.value() / 10
            image = ImageEnhance.Brightness(image).enhance(factor)

            # Update Sharpness
            factor = self.sharpness_slider.value() / 10
            image = ImageEnhance.Sharpness(image).enhance(factor)

            pixmap = self.__img_2_pixmap(image, mode)
            self.textBrowser_2.setPixmap(pixmap)
        except Exception as e:
            print(e)

    def __img_2_pixmap(self, image, mode):
        image = np.array(image)
        if mode == "RGB":
            format = QtGui.QImage.Format_RGB888
        else:
            format = QtGui.QImage.Format_Indexed8
        image = QtGui.QImage(image.data, image.shape[1], image.shape[0], format)

        return QtGui.QPixmap.fromImage(image)


